import React from "react";


import {SearchBar} from "../SearchBar/SearchBar";
import {SearchResults} from "../SearchResults/SearchResults";
import {Playlist} from "../Playlist/Playlist";
import {PlayMusic} from "../PlayMusic/PlayMusic";
import Spotify from "../util/Spotify";

import './App.css'

export class App extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            searchResults :
                [],
            playList:
                [],
            playlistName: "New Playlist"
        }

        this.addTrack = this.addTrack.bind(this)
        this.removeTrack = this.removeTrack.bind(this)
        this.changeName = this.changeName.bind(this)
        this.savePlayList = this.savePlayList.bind(this)
        this.search = this.search.bind(this)
    }

    addTrack(track){
        let tracks = this.state.playList
        if(!this.state.playList.find(e=>e.id===track.id)){
            tracks.push(track)
            this.setState({
                playList:tracks
            })
        }
    }

    removeTrack(track){
        let tracks = this.state.playList.filter(
            e => e.id !== track.id
        )
        this.setState({
            playList:tracks
        })
    }

    changeName(value){
        this.setState({
            playlistName: value
        })
    }

    // spotify:track:3IAfUEeaXRX9s9UdKOJrFI
    savePlayList(){
        const trackURIs = this.state.playList.map(track=>track.uri)
        Spotify.savePlayList(this.state.playlistName,trackURIs).then(()=>{
            this.setState({
                playlistName:"New Playlist",
                playList : []
            })
        })
    }

    search(term){
      Spotify.search(term).then(
          searchResults =>{
              this.setState({
                  searchResults:searchResults
              })
          }
      )
    }


    render() {
        return(
                <div>
                    <h1>Ja<span className="highlight">mmm</span>ing</h1>
                    <div className="App">
                        <SearchBar onSearch={this.search}/>
                        <PlayMusic src={"75rF4v7ode0VF02i7IQL9b"}/>
                        <div className="App-playlist">
                            <SearchResults searchResults={this.state.searchResults} addTrack={this.addTrack} />
                            <Playlist
                                searchResults={this.state.playList}
                                removeTrack={this.removeTrack}
                                playlistName={this.state.playlistName}
                                changeName = {this.changeName}
                                onSave = {this.savePlayList}
                            />
                        </div>
                    </div>
                </div>
        )
    }
}
