import React from "react"
import './Track.css'
import play from './play-button-svgrepo-com.svg'
export class Track extends React.Component{

    renderAction(){
        if(this.props.isRemoval){
            return <button className="Track-action" onClick={()=>{this.props.removeTrack(this.props.track)}}>-</button>
        }else{
            return(
                <React.StrictMode>
                    <button className="Track-action" onClick={()=>{this.props.addTrack(this.props.track)}}>+</button>
                    <button>
                        <svg viewBox="0 0 26 26" xmlns="http://www.w3.org/2000/svg" style="width: 100%; height: 100%; color: white;"><title>Ph√°t</title><path d="M7.712 22.04a.732.732 0 0 1-.806.007.767.767 0 0 1-.406-.703V4.656c0-.31.135-.544.406-.703.271-.16.54-.157.806.006l14.458 8.332c.266.163.4.4.4.709 0 .31-.134.546-.4.71L7.712 22.04z" fill="currentColor" fill-rule="evenodd"></path></svg>
                    </button>
                </React.StrictMode>
            )
        }
    }



    render() {
        return(
            <div className="Track">
                <div className="Track-information" >
                    <h3>{this.props.track.name}</h3>
                    <p>{this.props.track.artist}</p>

                </div>

                {this.renderAction()}

            </div>
        )
    }
}
